$(document).ready(function(){if($('.form--registration').length>0){if(form_title){$('[name="hidden-page"]').val(form_title);}}
var current_sub_page=1;$(document).on('click','#load_more',function(e){e.preventDefault();var data={'action':'load_more','term_id':$(this).data('category'),'current':current_sub_page}
$.post(ajax_url,data,function(response){if(response){response=JSON.parse(response);$(response.html).appendTo('.grid');if(response.count<6){$('#load_more').hide();}}});current_sub_page+=1;})
if($('.registration').length>0){$('[name="seminar"]').parents('.form__field--select').hide();$('[name="brand"]').parents('.form__field--select').hide();$('[name="role"]').on('change',function(){var val=$(this).val();if(val=='1'||val=='2'){$('[name="seminar"]').parents('.form__field--select').show('fast');$('[name="brands"]').parents('.form__field--select').hide('fast');}else if(val=='3'){$('[name="brand"]').parents('.form__field--select').show('fast');$('[name="seminar"]').parents('.form__field--select').hide('fast');}else{$('[name="seminar"]').parents('.form__field--select').hide('fast');$('[name="brands"]').parents('.form__field--select').hide('fast');}})}});